import { useState, useEffect } from 'react';
import TodoItem from './TodoItem';



const TodoForm = ({ onSave, onDelete, onGetTodos }) => {
  const [topic, setTopic] = useState('');
  const [body, setBody] = useState('');
  const [todos, setTodos] = useState({});
  const [editingTodo, setEditingTodo] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const createTodo = async (todo) => {
    try {
      const response = await fetch('http://localhost:8082/emotions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(todo),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const createdTodo = await response.json();
      return createdTodo;
    } catch (error) {
      console.error('Error creating todo:', error);
      throw error;
    }
  };

  const deleteTodo = async (id) => {
    try {
      const response = await fetch(`http://localhost:8082/emotions/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json();
    } catch (error) {
      console.error('Error deleting todo:', error);
      throw error;
    }
  };


  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const response = await fetch('http://localhost:8082/emotions/user');
        const data = await response.json();
        setTodos(data);
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };

    fetchTodos();
  }, []);

  useEffect(() => {
    if (editingTodo) {
      const { topic: editingTopic, body: editingBody } = editingTodo;
      setTopic(editingTopic);
      setBody(editingBody);
    }
  }, [editingTodo]);

  const handleTopicChange = (event) => {
    setTopic(event.target.value);
  };

  const handleBodyChange = (event) => {
    setBody(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const todoToSave = { topic, body, ...(todos && { id: todos.id }) };
      const createdTodo = await createTodo(todoToSave);
      onSave(createdTodo);
      setTopic('');
      setBody('');
      setTodo({});
    } catch (error) {
      console.error('Error creating todo:', error);
    }
  };

  const handleAddTodoClick = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingTodo(null);
  };

  const handleTodoClick = (todo) => {
    setEditingTodo(todo);
    setShowModal(true);
  };

  return (
      <div>
        <h1 className="h1">Заметки</h1>
        <ul className="ul">
          {todos.length > 0 ? (
              todos.map((todo) => (
                  <TodoItem
                      key={todo.id}
                      todo={todo}
                      onDelete={() => deleteTodo(todo.id)}
                      onClick={() => handleTodoClick(todo)}
                  />
              ))
          ) : (
              <div></div>
          )}
        </ul>
        <button className="plus" onClick={handleAddTodoClick}>
          +
        </button>
        {showModal && (
            <div className="modal">
              <div className="modal-content">
                <form onSubmit={handleSubmit}>
                  <label htmlFor="todoText">Todo text:</label>
                  <input
                      type="text"
                      id="todoText"
                      name="todoText"
                      defaultValue={editingTodo?.text ?? ''}
                      required
                  />
                  <button type="submit">Save Todo</button>
                  <button type="button" onClick={handleCloseModal}>
                    Close
                  </button>
                </form>
              </div>
            </div>
        )}
      </div>
  );
};

export default TodoForm;
