import * as React from 'react';
import  { useState } from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';


function Copyright(props) {
    return (
        <Typography variant="body2" color="text.secondary" align="center" {...props}>
            {'Copyright © '}
            <Link color="inherit" href="https://mui.com/">
                Your Website
            </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const defaultTheme = createTheme();


export default function SingIn() {
    const initialState = {
        email: '',
        password: '',
        firstName: '',
        allowExtraEmails: false,
    };
    const [formData, setFormData] = useState(initialState);
    const [signIn, setSignIn] = useState(true);
    const [user, setUser] = useState(null);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({...prevFormData, [name]: value }));
    };

    const handleLogin = async (event) => {
        event.preventDefault();
        try {
              const response = await fetch('http://auth-service:8081/login', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
                  });
              if (response.ok) {
                    const user = { email: formData.email };
                    console.log('Authentication successful');
                    setUser(user);
                  } else {
                    throw new Error('Authentication failed');
                  }
            } catch (error) {
              console.error('Error during authentication:', error);
              throw error;
        }
    };

    const handleRegister = async (event) => {
        event.preventDefault();
        try {
              const response = await fetch('http://auth-service:8081/register', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
                  });
              if (response.ok) {
                    console.log('Registration successful');
                  } else {
                    throw new Error('Registration failed');
                  }
            } catch (error) {
              console.error('Error during registration:', error);
              throw error;
            }
      };

      const handleSignUpClick = () => {
            setSignIn(false);
          };

      const handleSignInClick = () => {
            setSignIn(true);
          };

    return (
        <>
            {signIn ? (
                <ThemeProvider theme={defaultTheme}>
                        <Container component="main" maxWidth="xs">
                            <CssBaseline />
                            <Box
                                sx={{
                                    marginTop: 8,
                                    display: 'flex',
                                    flexDirection: 'column',
                                    alignItems: 'center',
                                }}
                            >
                                <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                                    <LockOutlinedIcon />
                                </Avatar>
                                <Typography component="h1" variant="h5">
                                    Sign in
                                </Typography>
                                <Box component="form" onSubmit={handleLogin}  noValidate sx={{ mt: 1 }}>
                                    <TextField
                                        margin="normal"
                                        required
                                        fullWidth
                                        id="email"
                                        label="Email Address"
                                        name="email"
                                        autoComplete="email"
                                        value={formData.email}
                                        onChange={handleInputChange}
                                    />
                                    <TextField
                                        margin="normal"
                                        required
                                        fullWidth
                                        name="password"
                                        label="Password"
                                        type="password"
                                        id="password"
                                        autoComplete="current-password"
                                        value={formData.password}
                                        onChange={handleInputChange}
                                    />
                                    <FormControlLabel
                                        control={<Checkbox value="remember" color="primary" />}
                                        label="Remember me"
                                    />
                                    <Button
                                        type="submit"
                                        fullWidth
                                        variant="contained"
                                        sx={{ mt: 3, mb: 2 }}
                                    >
                                        Sign In
                                    </Button>
                                    <Grid container>
                                        <Grid item xs>
                                            <Link href="#" variant="body2">
                                                Forgot password?
                                            </Link>
                                        </Grid>
                                        <Grid item>
                                            <Link href="#" variant="body2" onClick={handleSignUpClick}>
                                                {"Don't have an account? Sign Up"}
                                            </Link>
                                        </Grid>
                                    </Grid>
                                </Box>
                            </Box>

                        </Container>
                    )
                </ThemeProvider>
            ) : (
                <ThemeProvider theme={defaultTheme}>
                    <Container component="main" maxWidth="xs">
                        <CssBaseline />
                        <Box
                            sx={{
                                marginTop: 8,
                                display: 'flex',
                                flexDirection: 'column',
                                alignItems: 'center',
                            }}
                        >
                            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                                <LockOutlinedIcon />
                            </Avatar>
                            <Typography component="h1" variant="h5">
                                Sign up
                            </Typography>
                            <Box component="form" onSubmit={handleRegister} sx={{mt: 3}}>


                                    <form onSubmit={handleRegister} className="register-form">
                                    <label htmlFor="firstName" className="form-label">First Name:</label>
                                    <input
                                        type="text"
                                        name="firstName"
                                        id="firstName"
                                        value={formData.firstName}
                                        onChange={handleInputChange}
                                        required
                                        className="form-input"
                                    />
                                    <br/>

                                    <label htmlFor="email" className="form-label">Email:</label>
                                    <input
                                        type="email"
                                        name="email"
                                        id="email"
                                        value={formData.email}
                                        onChange={handleInputChange}
                                        required
                                        className="form-input"
                                    />
                                    <br/>

                                    <label htmlFor="password" className="form-label">Password:</label>
                                    <input
                                        type="password"
                                        name="password"
                                        id="password"
                                        value={formData.password}
                                        onChange={handleInputChange}
                                        required
                                        className="form-input"
                                    />
                                    <br/>

                                    <input
                                        type="checkbox"
                                        name="allowExtraEmails"
                                        id="allowExtraEmails"
                                        onChange={handleInputChange}
                                        className="form-checkbox"
                                    />
                                    <label htmlFor="allowExtraEmails" className="form-label">Receive promotional
                                        emails</label>
                                    <br/>

                                    <button type="submit" onClick={handleRegister} className="form-submit">Register
                                    </button>
                                </form>

                                <Grid container justifyContent="flex-end">
                                    <Grid item>
                                        <Link href="#" variant="body2" onClick={handleSignInClick}>
                                            {signIn ? "Don't have an account? Sign Up" : "Already have an account? Sign in"}
                                        </Link>
                                    </Grid>
                                </Grid>

                        </Box>
                    </Box>
                    <Copyright sx={{mt: 5}}/>
                </Container>
                </ThemeProvider>
                )}
</>
)
;
}
