import React, { useState } from 'react';
import "../App.css"
import { Trash2} from "lucide-react";

function Todo() {
    const [todos, setTodos] = useState([
        { id: 1, text: 'Learn React' },
        { id: 2, text: 'Build a todo app' },
        { id: 3, text: 'Deploy the app' },
    ]);

    function addTodo(text) {
        const id = todos.length + 1;
        setTodos([...todos, { id, text }]);
    }

    const [showModal, setShowModal] = useState(false);
    const [editingTodo, setEditingTodo] = useState(null);

    function handleAddTodoClick() {
        setShowModal(true);
    }

    function handleCloseModal() {
        setShowModal(false);
        setEditingTodo(null);
    }

    function handleSubmit(event) {
        event.preventDefault();
        const text = event.target.todoText.value;
        if (editingTodo!== null) {
            // Update existing todo
            const updatedTodos = todos.map(todo => {
                if (todo.id === editingTodo.id) {
                    return {...todo, text };
                }
                return todo;
            });
            setTodos(updatedTodos);
        } else {
            // Add new todo
            addTodo(text);
        }
        handleCloseModal();
    }
    const deleteTodo = (roomNumber) => {
        const updatedList = todos.filter((todo) => todo.id !== roomNumber);
        setTodos(updatedList);
    };

    const handleTodoClick = (todo) => {
        setEditingTodo(todo);
        setShowModal(true);
    };

    return (
        <div>
            <h1 className="h1">Заметки</h1>
            <ul className="ul">
                {todos.map((todo) => (
                    <TodoItem key={todo.id} todo={todo} onDelete={() => deleteTodo(todo.id)} onClick={() => handleTodoClick(todo)} />
                ))}
            </ul>
            <button className="plus" onClick={handleAddTodoClick}>+</button>
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <form onSubmit={(e) => handleSubmit(e)}>
                            <label htmlFor="todoText">Todo text:</label>
                            <input type="text" id="todoText" name="todoText" defaultValue={editingTodo ? editingTodo.text : ''} required />
                            <button type="submit">Save Todo</button>
                            <button type="button" onClick={handleCloseModal}>
                                Close
                            </button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

function TodoItem({ todo, onDelete, onClick }) {
    return (
        <li className="li">
            <p style={{width:"90%",margin:"0",padding:"10px"}} onClick={onClick}>{todo.text }</p>
            <Trash2 size={20} className="icon" onClick={onDelete} />
        </li>
    );
}

export default Todo;
